import { useState, useEffect } from "react";

function App() {
  const [toDo, setToDo] = useState("");
  const [toDoList, setTodoList] = useState([]);
  const onChange = (e) => setToDo(e.target.value);
  console.log('1. ', toDoList, '.........', toDo);
  const onSubmit = (e) => {
    e.preventDefault();
    console.log(toDo);
    if (toDo === "") {
      return;
    }
    // if (!toDoList.length) {
    //   setTodoList([toDo]);
    // } else {
      console.log('2. ', toDoList, '.........', toDo);
      setTodoList(current => [toDo, ...current]);
    // }
    console.log('3. ', toDoList, '.........', toDo);
    setToDo('');
    console.log('4. ', toDoList, '.........', toDo);
  }

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input
          onChange={onChange}
          value={toDo}
          type="text"
          placeholder="Write your to do..."
        />
        <button>Add To Do</button>
      </form>
    </div>
  );
}

export default App;
