import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import "./Movie.css";
import { useEffect, useState } from "react";

function Movie({ id, coverImg, title, summary, genres }) {
  const [showCover, setShowCover] = useState(false);
  const hoverEvent = () => {
    setShowCover(true);
  }
  const hoverOutEvent = () => {
    setShowCover(false);
  }
  return (
    <div className="movie_card">
      <Link to={`/movie/${id}`}>
      <span class="image_box">
        <img
          src={coverImg}
          onMouseEnter={hoverEvent}
          onMouseLeave={hoverOutEvent}
        />
        {showCover && <div className="image_cover">
          <button className="detail_button">Detail</button>
        </div>}
      </span>
      </Link>
      <div className="movie_title">{title}</div>
      { summary && <p>{summary}</p>}
      <div className="tag_box">
        { genres.map((g, gi) => <span key={gi} className="tag">{g}</span>) }
      </div>
    </div>
  );
}

Movie.propTypes = {
  id: PropTypes.number.isRequired,
  coverImg: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  summary: PropTypes.string,
  genres: PropTypes.arrayOf(PropTypes.string).isRequired
};

export default Movie;
